import React from 'react';

import AppHeader from "../app-header/app-header";
import SearchPanel from "../search-panel/search-panel";
import PostStatusFilter from "../post-status-filter/post-status-filter";
import PostList from "../post-list/post-list";
import PostAddForm from "../post-add-form/post-add-form";

import './app.css';
import style from './App.module.css';
import styled from "styled-components";

const AppBlock = styled.div`
    max-width: 800px;
    margin: 0 auto;
`;
const StyledAppBlock = styled(AppBlock)`
//background-color: gray;
`;

const myPosts = [
  {
    label: "going to learn React",
    important: false,
    id: "aaw1"
  },
  {
    label: "need to learn faster",
    important: false,
    id: "aaw2"
  },
  {
    label: "i must finish first 2 folders to go for a meat baking",
    important: true,
    id: "aaw3"
  },
];

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myPosts : [
        {
          label: "going to learn React",
          important: false,
          id: "aaw1"
        },
        {
          label: "need to learn faster",
          important: false,
          id: "aaw2"
        },
        {
          label: "i must finish first 2 folders to go for a meat baking",
          important: true,
          id: "aaw3"
        },
        {
          label: "rock isn't so bad",
          id: "aaw4"
        },
      ]
    };
    this.deleteItem = this.deleteItem.bind(this)
    this.addItem = this.addItem.bind(this)

    this.maxId = 4;
  }

  deleteItem(id) {
    this.setState(({ myPosts }) => {
      const index = myPosts.findIndex( elem => elem.id === id );
      console.log("index: " + index + "; id: " + id);
      // myPosts.slice(index, 1);
      // return {
      //   myPosts: myPosts
      // };
      const before = myPosts.slice(0, index);
      const after = myPosts.slice(index + 1);

      const newArr = [...before, ...after];

      return {
        myPosts: newArr
      }
    });
  };

  addItem(body) {
    const newItem = {
      label: body,
      important: false,
      id: this.maxId++
    }
    this.setState((state) => {
      const newArr = [...state.myPosts, newItem]
    })
  }

  render() {
    return (
        <StyledAppBlock >
          {/*<div className={ style.app } >*/}
          {/*<div className="app" >*/}
          <AppHeader/>
          <div className="search-panel d-flex">
            <SearchPanel/>
            <PostStatusFilter/>
          </div>
          <PostList posts = { this.state.myPosts }
                    onDelete = { this.deleteItem }/>
          <PostAddForm onAdd={ this.addItem }/>
        </StyledAppBlock>
    )
  }
};
