import React from "react";

import './post-status-filter.css'
import { Button } from 'reactstrap';

export default class PostStatusFilter extends React.Component {
  constructor(props) {
    super(props);
    this.buttons = [
      {name: 'all', label: 'Everything'},
      {name: 'liked', label: 'Liked'},
    ]
  }


  render() {
    const buttons = this.buttons.map( ({name, label}) => { // вытаскиваем чтобы не писать итем.лейбл...
      return (
          <button key={ name } className="btn btn-info">{ label }</button>
      )
    });

    return (
        <div className="btn-group">

          {/*<button className="btn btn-info">Everything</button>*/}
          {/*<Button outline color="info">Liked</Button>*/}
          {/*<button className="btn btn-outline-secondary">Liked</button>*/}
        </div>
    )
  }
};
